INSERT INTO `ux73_content` VALUES ('157', '291431351307', '101427698727', '', '', '291431350032', '', '最后的香格里拉\r<br />-------------------------------\r<br />其实最早以前的香格里拉并非叫“香格里拉”，而是中甸提起香格里拉，一定有很多人记得2014年1月中的那场大火，246户受灾，343栋房屋被烧毁，那场大火烧毁了1/3的香格里拉古城-独克宗也随着这场大火，香格里拉渐渐淡出了游客的视野中\r<br />\r<br />反季节游是我的最爱，在无人问津的季节，我来到了这里\r<br />\r<br />很多人在香格里拉会选择住独克宗古城，但是我却偏偏选择了在伊拉草原上面，晚上听不见来自各个清吧及酒吧的声音，走出客栈，就是草原。\r<br />', '', '', '', '', '', '0', null, '0', '0', '1431351307', '1431351307', '0', '0', '2147483647', '7', '0', '0', '1431541290', '14315388050');
