INSERT INTO `ux73_content` VALUES ('171', '271431494976', '201427430248', '', '', '271431493946', '', '如今摄影的边界越来越模糊，已经不能简单准确的定义，摄影只是一个概念。摄影艺术已经和其他艺术形式形成交叉的结合，有时候很难称之为摄影，或者叫做视觉艺术更合适。拍摄只是创作的一种方法，照相机只是一种工具，只要能创作出唯美的作品，如何制作都不重要。正如画家作画一样，不能只有写生。', '', 'http://webimg1.meitudata.com/201505/13/5552e13d776ea.jpg', '', '', '', '0', null, '0', '66', '1431494976', '1431494976', '0', '1', '2147483647', '52', '4', '1', '0', '0');
