INSERT INTO `ux73_content` VALUES ('192', '271432098075', '201427430248', null, null, '271432097636', null, '来自俄罗斯的人像摄影师Корнеев Виктор，擅长情绪人像，环境人像。他是摄影风格低调内敛，色调以冷色调为主。他善于捕捉人物的神态，气质和情绪，在简单的画面中营造出故事感，对于人物肖像的表现力强，在眼神的刻画上更加到位。从整体风格上，可以感受到浓郁的复古气息和文艺电影味道。...（作者：Корнеев Виктор 2015-05-20）', 'http://webimg1.meitudata.com/201505/20/555c151845a41.jpg', null, null, null, null, '0', null, '1', '67', '0', '1432098075', null, '0', '1', null, '25', '4', '1', '0', '0');
