INSERT INTO `ux73_content` VALUES ('213', '291432191575', '101427698727', null, null, '291431350032', null, '旅途总是充满了未知，或许是惊喜。因为太高兴了，Isabella没有看见路上的一个大坑，车子一阵强烈的颠簸过后，爆胎。\r<br />是的，这张照片是我正在推车，在山路上推电瓶车。我们已经离客栈非常远了，想着搭车回去吧，结果由于电瓶车太大，很多车都装不下。\r<br />天公不作美，天气阴天加下雨，是的，我们在风雨中前行半小时，终于一辆皮卡车将我们连车带人送回客栈，在这里要感谢那位好心的大哥，祝愿他能一切都好。', 'http://webimg1.meitudata.com/201505/21/555d8249efce8.jpg', 'http://webimg1.meitudata.com/201505/21/555d8249efce8.jpg', null, null, null, '0', null, '3', '109', '0', '1432191575', null, '0', '1', null, '52', '0', '1', '1432192190', '14321920446');
