INSERT INTO `ux73_content` VALUES ('258', '271435203544', '201427430248', null, null, '271435202834', null, '人品杠杠的五月川西行摄之旅 \r<br />5月17日至5月26日，跟随高大的脚步，行摄于川西环线，沿途风光美不胜收，人品杠杠的本团，经历了火烧云、电筒光、清晰震撼的银河等等场景，吃到了野生鱼、牦牛火锅、柴火鸡等地道美食，可谓精彩丰富。借本帖表达对辛苦带领我们的高大、飞翔、闲茶三位领队的感谢。下面分三部分介绍本次行摄之旅，一、风光，二、人文，三、拍摄花絮。 \r<br />', 'http://webimg1.meitudata.com/201506/25/558b77cba5104.jpg', null, null, null, null, '0', null, '5', '148', '0', '1435203544', null, '0', '1', null, '37', '4', '1', '0', '0');
